var jQueryScriptOutputted=false;var target_url=undefined;function getAniPalaceWidgetMagazin(a){if(target_url==undefined){if(a!=""){target_url=a}else{target_url="http://anipalace.hu/json/Widget-Magazin.json"}}if(typeof(jQuery)=="undefined"){if(!jQueryScriptOutputted){jQueryScriptOutputted=true;document.write('<script type="text/javascript" src="https://ajax.googleapis.com/ajax/libs/jquery/1.7.1/jquery.min.js"><\/script>')}setTimeout("getAniPalaceWidgetMagazin()",100)}else{jQuery(function(){jQuery.post(target_url,function(d){var e=jQuery.parseJSON(d);jQuery(".anipalace-widget").html(e.animagazin["image"]);var b="<div class='anipalace-widget anipalace-widget-animagazin'><a href='"+e.animagazin["link"]+"' title='"+e.animagazin["title"]+"'><img src='"+e.animagazin["image"]+"' alt='"+e.animagazin["title"]+"' /></a></div>";var c="<div class='anipalace-widget anipalace-widget-anistation'><a href='"+e.anistation["link"]+"' title='"+e.anistation["title"]+"'><img src='"+e.anistation["image"]+"' alt='"+e.anistation["title"]+"' /></a></div>";jQuery(".anipalace-widget").each(function(f){if(jQuery(this).attr("data-type")=="animagazin"){jQuery(this).html(b)}if(jQuery(this).attr("data-type")=="anistation"){jQuery(this).html(c)}if(jQuery(this).attr("data-type")=="all"){if(jQuery(this).attr("data-style")=="list"){jQuery(this).html("<div class='anipalace-widget-list'>"+b+c+"</div>")}if(jQuery(this).attr("data-style")=="changer"){jQuery(this).html("<div class='anipalace-widget-changer'>"+b+c+"<input type='button' class='anipalace-button' onclick='anipalace_change()' /></div>")}}})})})}};